import React, { useState } from 'react';

import MusemFormsField from '../museum-components/MuseumFormsField'
import MuseumBody from '../museum-components/MuseumBody'

import allTheData from '../museum-components/ThumbnailsData'

export default function Museums() {

      // RESULTS E FILTER.LENGTH
    const getTheData = allTheData

    const [filterThumbnails, setFilterThumbnails] = useState()
    const [currentDistrict, setCurrentDistrict] = useState()
    const [rangePrice, setRangePrice] = useState()

    // console.log(getTheData)

    const handleSelectInput = (e) => {
      const district = e.target.value
      district === 'all' ? setFilterThumbnails() : setFilterThumbnails(getTheData.filter(f => f.district === district))  
      setCurrentDistrict(getTheData.filter(f => f.district === district))    
  }

    const handleRangeInput = (e) => {
      const price = parseInt(e.target.value) 
      setRangePrice(price)
      // setFilterThumbnails(getTheData.filter(f=> f.price <= price)) - WORKS FOR ALL

      let getFilter = currentDistrict.filter(f => f.price <= price)
      setCurrentDistrict(getFilter)

    }


  return (
    <div className='museum-full-background-div'>
      <h1 className='search-museum-top-header'>Search Museums</h1>
      <div className='museum-line-top'></div>
      <MusemFormsField handleSelectInput={handleSelectInput} handleRangeInput={handleRangeInput} rangePrice={rangePrice} />
      <MuseumBody data={ filterThumbnails ? filterThumbnails :  getTheData} handleSelectInput={handleSelectInput}/>
    </div>
  );
}
