import React, { useState } from 'react';

import MusemFormsField from '../museum-components/MuseumFormsField'
import MuseumBody from '../museum-components/MuseumBody'

import allTheData from '../museum-components/ThumbnailsData'

export default function Museums() {

    const [museumTotal, setMuseumTotal] = useState({
      museum: allTheData,
      sortedMuseums: [],
      district: 'all',
      price: 0,
      minPrice: 10,
      maxPrice: 200,
      kids: false,
      free: false
    })



    const handleChange = (e) => {
      const target = e.target
      const value = e.type === 'checkbox' ? target.checked : target.value
      const name = e.target.name 
       setMuseumTotal({
         [name]:value
       }, this.filterRooms)
      // console.log(`this is type: ${type}, this is name: ${name}, this is value: ${value}`)
    }

    const filterRooms = () => {
       console.log('i am filterRoom function')
    }

  return (
    <div className='museum-full-background-div'>
      <h1 className='search-museum-top-header'>Search Museums</h1>
      <div className='museum-line-top'></div>
      <MusemFormsField museumTotal={museumTotal} handleChange={handleChange}   />
      <MuseumBody sortedMuseums={ museumTotal.sortedMuseums} />
    </div>
  );
}











