import React, { useState, useEffect } from 'react';

import MusemFormsField from '../museum-components/MuseumFormsField'
import MuseumBody from '../museum-components/MuseumBody'

import allTheData from '../museum-components/ThumbnailsData'

export default function Museums() {

  const [sortedMuseum, setSortedMuseum] = useState(allTheData)
  // console.log(sortedMuseum)

  const [museumTotal, setMuseumTotal] = useState({
    museum: allTheData,
    district: 'all',
    price: 0,
    minPrice: 10,
    maxPrice: 200,
    kids: false,
    free: false
  })

  const [test, setTest] = useState(true)

  const handleChange = (e) => {
    // const target = e.target
    const value = e.type === 'checkbox' ? e.target.checked : e.target.value
    const name = e.target.name

    // console.log(`target: i am ${target}, value: i am ${value}, name: i am ${name}`)
    setMuseumTotal({
      ...museumTotal,
      [name]: value,
    }, filterRooms(value, name))
  }

  const filterRooms = (value, name) => {
    //  let { museum, district, price, minPrice, maxPrice, kids, free } = museumTotal
    let { museum } = museumTotal

    let tempMuseum = [...museum]

    console.log(value)
    console.log(name, typeof name)

    if (name === 'price') {
      var pricing = parseInt(value)
    }

    switch (name) {
      case 'district':
        if (value !== 'all') {
          tempMuseum = tempMuseum.filter(m => m.district === value)
        }
        break;
        case 'price':
          // console.log(price)
        tempMuseum = tempMuseum.filter(m => m.price <= price)
    }

    // console.log(price)


    console.log(tempMuseum)

    setSortedMuseum(tempMuseum)

    setTest(!test)

  }

  return (
    <div className='museum-full-background-div'>
      <h1 className='search-museum-top-header'>Search Museums</h1>
      <div className='museum-line-top'></div>
      <MusemFormsField museumTotal={museumTotal} handleChange={handleChange} />
      <MuseumBody sortedMuseum={sortedMuseum} />
    </div>
  );
}
