import React, { useState } from 'react';

import MusemFormsField from '../museum-components/MuseumFormsField'
import MuseumBody from '../museum-components/MuseumBody'

import allTheData from '../museum-components/ThumbnailsData'

export default function Museums() {

    const [museumTotal, setMuseumTotal] = useState({
      museum: allTheData,
      sortedMuseums: allTheData,
      district: 'all',
      price: 0,
      minPrice: 10,
      maxPrice: 200,
      kids: false,
      free: false
    })



    const handleChange = (e) => {
      const target = e.target
      const value = e.type === 'checkbox' ? target.checked : target.value
      const name = e.target.name 
       setMuseumTotal({
         ...museumTotal,
         [name]:value
       }, filterRooms())
    }

    const filterRooms = () => {
       let { museum, district, price, minPrice, maxPrice, kids, free } = museumTotal

       let tempMuseum = [...museum]
       if (district !== 'all') {
          tempMuseum = tempMuseum.filter(m => m.district === district )
       }

       setMuseumTotal({
         ...museumTotal,
         sortedMuseums: tempMuseum
       })
    }

  return (
    <div className='museum-full-background-div'>
      <h1 className='search-museum-top-header'>Search Museums</h1>
      <div className='museum-line-top'></div>
      <MusemFormsField museumTotal={museumTotal} handleChange={handleChange}   />
      <MuseumBody museumTotal={museumTotal} />
    </div>
  );
}











